import React, { Component } from 'react';

class Body extends Component {
    constructor() {
        super()
        this.state = {
            search: "",
            tvShows: [],
        }   
        this.doSearch();     
    }

    async componentDidMount() {
        
    }

    doSearch = async (showName = 'Loui') => {
        const res = await fetch(`http://api.tvmaze.com/search/shows?q=${showName}&limit=2`);
        const response = res.slice(0, 2);
        const jsonData = await response.json();
        //console.log(jsonData);
        this.setState({ tvShows: jsonData });
    }

    render() {
        return (
            <div>
                 {this.state.tvShows.map(show => { 
                return (
                <div>
                    <div><h2><span>{show.show.name}</span><span> "sp"</span></h2></div>
                    <div dangerouslySetInnerHTML={{__html: show.show.summary}} />
                 </div> 
                 )})}
            </div>            
        )
    }

    // render() {
    //     return (
    //         <div>
    //              {this.state.tvShows.map(show => { 
    //             return (
    //             <div>
    //                 <div><a href={show.show.image.medium} ><h2>{show.show.name}</h2></a> </div>
    //                 <div><img src={show.show.image.medium} alt=""></img></div>
    //                 <div dangerouslySetInnerHTML={{__html: show.show.summary}} />
    //              </div> 
    //              )})}
    //         </div>            
    //     )
    // }
}

export default Body;
